mapper.insert:
insert into pet (category, name, tags, 
  price, status, photoUrls
  )
values (#{category,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{tags,jdbcType=INTEGER}, 
  #{price,jdbcType=INTEGER}, #{status,jdbcType=VARCHAR}, #{photoUrls,jdbcType=LONGVARCHAR}
  )

mapper.select:
select id, category, name, tags, price, status, photoUrls
from pet
where id = #{id,jdbcType=INTEGER}
